import styled from 'styled-components';

export const Container = styled.div`
  display: grid;
  grid-template-columns: repeat(6, 1fr);
  grid-template-rows: 1fr;
  grid-column-gap: 24px;
  grid-row-gap: 0px;
`;

export const Cards = styled.div`
  width: 180px;
  height: 240px;
  border-radius: 8px;

  a{
    position: relative;
  }

  .infos{
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 120px;
    background: linear-gradient(180deg, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.7) 67.08%);
    border-radius: 8px;

    #title{
      color: #ffffff;
    }
  }
`;
